PictureData.Code:
var camAngle = mz3d.blendCameraYaw["value"];
camAngle = Math.round(mz3d.blendCameraYaw["value"] / 45) * 45;

if ( camAngle < 0 ) { 
	camAngle += 360; 
};
if ( camAngle == 360 ) { 
	camAngle = 0; 
};

var compassFile = "img/pictures/MapCompass" + Math.abs(camAngle) + ".png";

return compassFile;

