// Get boss HP based on variable
// unless -1, then use whole troop
var bossHP = 0;
var bossID = $gameVariables._data[11]-1;
if ($gameTroop.members()[bossID]) {
	bossHP = $gameTroop.members()[bossID].mhp;
} else {
	$gameTroop.members().forEach(boss => bossHP += boss.mhp);
};
return bossHP;

